@charset "UTF-8";
/**
 * 🎨 金融数据终端设计系统 v2.0
 * 美学方向: Bloomberg Terminal + TradingView + 现代Fintech
 * 核心特质: 高密度数据展示、深色主题、高对比度、精确网格
 */
/* ========================================
   1. CSS变量定义 - 设计令牌
   ======================================== */
:root {
  /* 🎨 主色调系统 */
  --fintech-bg-primary: #0a0e27; /* 深蓝黑 - 主背景 */
  --fintech-bg-secondary: #131829; /* 次级背景 */
  --fintech-bg-tertiary: #1a1f38; /* 三级背景 */
  --fintech-bg-elevated: #232946; /* 浮层背景 */
  /* 🌈 强调色系统 */
  --fintech-accent-primary: #1890ff; /* 科技蓝 - 主强调 */
  --fintech-accent-success: #52c41a; /* 跌绿（中国习惯） */
  --fintech-accent-danger: #f5222d; /* 涨红（中国习惯） */
  --fintech-accent-warning: #faad14; /* 警告黄 */
  --fintech-accent-info: #13c2c2; /* 信息青 */
  /* ⚫ 黑白灰度系统（10级精确灰度） */
  --fintech-gray-1: #ffffff; /* 纯白 */
  --fintech-gray-2: #f0f0f0;
  --fintech-gray-3: #d9d9d9;
  --fintech-gray-4: #bfbfbf;
  --fintech-gray-5: #8c8c8c;
  --fintech-gray-6: #595959;
  --fintech-gray-7: #434343;
  --fintech-gray-8: #262626;
  --fintech-gray-9: #1f1f1f;
  --fintech-gray-10: #000000; /* 纯黑 */
  /* 📏 文字颜色系统 */
  --fintech-text-primary: rgba(255, 255, 255, 0.95);
  --fintech-text-secondary: rgba(255, 255, 255, 0.75);
  --fintech-text-tertiary: rgba(255, 255, 255, 0.55);
  --fintech-text-disabled: rgba(255, 255, 255, 0.35);
  /* 🔲 边框颜色系统 */
  --fintech-border-light: rgba(255, 255, 255, 0.15);
  --fintech-border-base: rgba(255, 255, 255, 0.10);
  --fintech-border-dark: rgba(255, 255, 255, 0.05);
  /* 🌊 阴影系统（多层次深度） */
  --fintech-shadow-sm: 0 1px 2px 0 rgba(0, 0, 0, 0.3);
  --fintech-shadow-base: 0 2px 8px 0 rgba(0, 0, 0, 0.4);
  --fintech-shadow-md: 0 4px 12px 0 rgba(0, 0, 0, 0.5);
  --fintech-shadow-lg: 0 8px 24px 0 rgba(0, 0, 0, 0.6);
  --fintech-shadow-xl: 0 16px 48px 0 rgba(0, 0, 0, 0.7);
  /* 🎯 圆角系统（专业微圆角） */
  --fintech-radius-none: 0;
  --fintech-radius-sm: 2px;
  --fintech-radius-base: 4px;
  --fintech-radius-md: 6px;
  --fintech-radius-lg: 8px;
  /* 📐 间距系统（4px基准网格） */
  --fintech-space-0: 0;
  --fintech-space-1: 4px;
  --fintech-space-2: 8px;
  --fintech-space-3: 12px;
  --fintech-space-4: 16px;
  --fintech-space-5: 20px;
  --fintech-space-6: 24px;
  --fintech-space-8: 32px;
  --fintech-space-10: 40px;
  --fintech-space-12: 48px;
  /* ⚡ 字体系统 */
  --fintech-font-family-data: 'JetBrains Mono', 'Consolas', 'Monaco', monospace;
  --fintech-font-family-ui: 'Noto Sans SC', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  --fintech-font-family-number: 'Roboto Mono', 'Courier New', monospace;
  /* 📏 字体大小 */
  --fintech-font-size-xs: 11px;
  --fintech-font-size-sm: 12px;
  --fintech-font-size-base: 13px;
  --fintech-font-size-md: 14px;
  --fintech-font-size-lg: 16px;
  --fintech-font-size-xl: 18px;
  --fintech-font-size-2xl: 20px;
  --fintech-font-size-3xl: 24px;
  /* 🔄 过渡动画 */
  --fintech-transition-fast: 150ms cubic-bezier(0.4, 0, 0.2, 1);
  --fintech-transition-base: 200ms cubic-bezier(0.4, 0, 0.2, 1);
  --fintech-transition-slow: 300ms cubic-bezier(0.4, 0, 0.2, 1);
}

/* ========================================
   2. 全局基础样式重置
   ======================================== */
* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

html,
body {
  width: 100%;
  height: 100%;
  overflow: hidden;
  /* 深色主题背景 */
  background-color: var(--fintech-bg-primary);
  color: var(--fintech-text-primary);
  /* 字体系统 */
  font-family: var(--fintech-font-family-ui);
  font-size: var(--fintech-font-size-base);
  line-height: 1.5;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

#app {
  width: 100%;
  height: 100%;
}

/* ========================================
   3. 滚动条样式（专业终端风格）
   ======================================== */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: var(--fintech-bg-secondary);
}

::-webkit-scrollbar-thumb {
  background: var(--fintech-gray-6);
  border-radius: var(--fintech-radius-sm);
}
::-webkit-scrollbar-thumb:hover {
  background: var(--fintech-gray-5);
}

::-webkit-scrollbar-corner {
  background: var(--fintech-bg-secondary);
}

/* ========================================
   4. 文字样式（语义化）
   ======================================== */
.fintech-text-primary {
  color: var(--fintech-text-primary);
}
.fintech-text-secondary {
  color: var(--fintech-text-secondary);
}
.fintech-text-tertiary {
  color: var(--fintech-text-tertiary);
}
.fintech-text-disabled {
  color: var(--fintech-text-disabled);
}
.fintech-text {
  /* 涨跌颜色（中国习惯：红涨绿跌） */
}
.fintech-text-up {
  color: var(--fintech-accent-danger);
  font-weight: 500;
}
.fintech-text-down {
  color: var(--fintech-accent-success);
  font-weight: 500;
}
.fintech-text-flat {
  color: var(--fintech-text-secondary);
}
.fintech-text {
  /* 数据字体（等宽字体优化对齐） */
}
.fintech-text-data {
  font-family: var(--fintech-font-family-data);
  font-feature-settings: "tnum";
  letter-spacing: 0.02em;
}

/* ========================================
   5. 背景样式
   ======================================== */
.fintech-bg-primary {
  background-color: var(--fintech-bg-primary);
}
.fintech-bg-secondary {
  background-color: var(--fintech-bg-secondary);
}
.fintech-bg-tertiary {
  background-color: var(--fintech-bg-tertiary);
}
.fintech-bg-elevated {
  background-color: var(--fintech-bg-elevated);
}

/* ========================================
   6. 卡片样式（多层次深度）
   ======================================== */
.fintech-card {
  background-color: var(--fintech-bg-secondary);
  border: 1px solid var(--fintech-border-base);
  border-radius: var(--fintech-radius-base);
  box-shadow: var(--fintech-shadow-sm);
  padding: var(--fintech-space-4);
}
.fintech-card.elevated {
  background-color: var(--fintech-bg-elevated);
  box-shadow: var(--fintech-shadow-base);
}
.fintech-card.interactive {
  transition: all var(--fintech-transition-base);
}
.fintech-card.interactive:hover {
  box-shadow: var(--fintech-shadow-md);
  transform: translateY(-1px);
}

/* ========================================
   7. 按钮样式（专业终端风格）
   ======================================== */
.fintech-btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: var(--fintech-space-2);
  padding: var(--fintech-space-2) var(--fintech-space-4);
  font-size: var(--fintech-font-size-base);
  font-weight: 400;
  border: 1px solid var(--fintech-border-base);
  border-radius: var(--fintech-radius-sm);
  background-color: var(--fintech-bg-tertiary);
  color: var(--fintech-text-primary);
  cursor: pointer;
  transition: all var(--fintech-transition-fast);
  user-select: none;
}
.fintech-btn:hover {
  background-color: var(--fintech-bg-elevated);
  border-color: var(--fintech-border-light);
}
.fintech-btn:active {
  transform: translateY(1px);
}
.fintech-btn.primary {
  background-color: var(--fintech-accent-primary);
  border-color: var(--fintech-accent-primary);
  color: #fff;
}
.fintech-btn.primary:hover {
  background-color: #40a9ff;
  border-color: #40a9ff;
}
.fintech-btn.danger {
  background-color: var(--fintech-accent-danger);
  border-color: var(--fintech-accent-danger);
  color: #fff;
}
.fintech-btn.danger:hover {
  background-color: #ff4d4f;
  border-color: #ff4d4f;
}
.fintech-btn.success {
  background-color: var(--fintech-accent-success);
  border-color: var(--fintech-accent-success);
  color: #fff;
}
.fintech-btn.success:hover {
  background-color: #73d13d;
  border-color: #73d13d;
}

/* ========================================
   8. 数据表格样式（高密度展示）
   ======================================== */
.fintech-table {
  width: 100%;
  border-collapse: collapse;
  font-size: var(--fintech-font-size-sm);
  font-family: var(--fintech-font-family-data);
}
.fintech-table th {
  padding: var(--fintech-space-2) var(--fintech-space-3);
  text-align: left;
  font-weight: 500;
  color: var(--fintech-text-secondary);
  border-bottom: 1px solid var(--fintech-border-base);
  background-color: var(--fintech-bg-tertiary);
  white-space: nowrap;
}
.fintech-table td {
  padding: var(--fintech-space-2) var(--fintech-space-3);
  color: var(--fintech-text-primary);
  border-bottom: 1px solid var(--fintech-border-dark);
}
.fintech-table tr {
  transition: background-color var(--fintech-transition-fast);
}
.fintech-table tr:hover {
  background-color: var(--fintech-bg-secondary);
}

/* ========================================
   9. 微动效系统
   ======================================== */
@keyframes fintech-fade-in {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}
@keyframes fintech-slide-up {
  from {
    opacity: 0;
    transform: translateY(8px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}
.fintech-animate-fade-in {
  animation: fintech-fade-in var(--fintech-transition-slow) ease-out;
}
.fintech-animate-slide-up {
  animation: fintech-slide-up var(--fintech-transition-slow) ease-out;
}

/* ========================================
   10. 响应式工具类
   ======================================== */
/* 仅桌面端（项目明确不支持移动端） */
@media (max-width: 1280px) {
  :root {
    --fintech-font-size-base: 12px;
  }
}
/* 高分辨率优化 */
@media (min-width: 1920px) {
  :root {
    --fintech-font-size-base: 14px;
  }
}

